function searchBox(e,t,l){let n=document.getElementById(e),o=document.getElementById(t),d=document.getElementById(l);const r=n.querySelectorAll("tr");d.addEventListener("change",()=>{for(let e=1;e<r.length;e++){let t=r[e],l=parseInt(d.value),n=t.querySelectorAll("td, th")[l],s=n.textContent.toLowerCase(),a=o.value.toLowerCase();s.includes(a)?t.style.display="":t.style.display="none"}})}